select u.User_Id, u.ERS_Username, u.User_First_Name, u.User_Last_Name, u.User_Email, ur.User_Role_Id, ur.role from ERS_User uinner join ERS_User_Roles uron u.User_Role_Id = ur.User_Role_Id where u.ERS_Username = 'john_smith' and u.ERS_Password = 'pass123';select r.Reimb_Id, r.Reimb_amount, r.Reimb_Submitted, r.Reimb_Resolved, r.Reimb_Description, r.Reimb_Receipt, u.User_Id, u.User_First_Name, u.User_Last_Name, u.User_Email,  usr.role, r.Reimb_Resolver, rt.Reimb_Type, rs.Reimb_Status 	from ERS_Reimbursement r 	inner join ERS_User u	on r.Reimb_Author = u.User_Id	inner join ERS_Reimbursement_Type rt 	on r.Reimb_Type_Id = rt.Reimb_Type_Id 	inner join ERS_Reimbursement_Status rs 	on r.Reimb_Status_Id = rs.Reimb_Status_Id	inner join ERS_User_Roles usr	on u.User_Role_Id = usr.User_Role_Id; 	select *from tickets;select *from ers_user;CREATE VIEW tickets as	select r.Reimb_Id, r.Reimb_amount, r.Reimb_Submitted, r.Reimb_Resolved, r.Reimb_Description, r.Reimb_Receipt, u.User_Id, u.User_First_Name, u.User_Last_Name, u.User_Email,  usr.role, r.Reimb_Resolver, rt.Reimb_Type, rs.Reimb_Status 	from ERS_Reimbursement r 	inner join ERS_User u	on r.Reimb_Author = u.User_Id	inner join ERS_Reimbursement_Type rt 	on r.Reimb_Type_Id = rt.Reimb_Type_Id 	inner join ERS_Reimbursement_Status rs 	on r.Reimb_Status_Id = rs.Reimb_Status_Id	inner join ERS_User_Roles usr	on u.User_Role_Id = usr.User_Role_Id; create view employees asSELECT u.User_id, u.User_First_Name, u.User_Last_Name, u.User_Email, usr.role, usr.User_Role_Id 	FROM ERS_User u	INNER JOIN ERS_User_Roles usr 	ON u.User_Role_Id = usr.User_Role_Id;SELECT * FROM employeesSELECT * FROM employees